//GEN-FIRST:event_loginadminActionPerformed
 private void loginadminActionPerformed(java.awt.event.ActionEvent evt) {                                         
        try{
            Statement statement = (Statement) conek.GetConnection().createStatement();
            ResultSet result = statement.executeQuery("select * from admin where " + "username='" + username.getText()+ "'" );
            if(result.next()){
                ResultSet result2 = statement.executeQuery("select * from admin where " + "password='" + password.getText()+"'");
                if(result2.next()){
                    new menuadmin(username.getText()).setVisible(true);
                    this.setVisible(false);
                }else{
                    JOptionPane.showMessageDialog(rootPane, "Password Salah");
                    password.setText("");
                    username.requestFocus();
                }
            }else{
                JOptionPane.showMessageDialog(rootPane, "User Tidak ditermukan");
                username.setText("");
                password.setText("");
                username.requestFocus();
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(rootPane, "gagal");
        }
           
    }
//GEN-LAST:event_loginadminActionPerformed

