    private void LOG_mhsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LOG_mhsActionPerformed
        try{
            Statement statement1 = (Statement) conek.GetConnection().createStatement();
            ResultSet result1 = statement1.executeQuery("select * from mahasiswa where " + "NIM='" +idlogin.getText()+"'" );
            if(result1.next()){
                new menuAnggota().setVisible(true);
                this.setVisible(false);
                }else{
                    JOptionPane.showMessageDialog(rootPane, "Anda belum terdaftar sebagai anggota atau Salah Submit");
                    idlogin.setText("");
                }
        }catch(Exception e){
            JOptionPane.showMessageDialog(rootPane, "gagal");
        }
           
    }//GEN-LAST:event_LOG_mhsActionPerformed

